import { View, Text, SafeAreaView, TextInput, ScrollView } from 'react-native'
import React, {useLayoutEffect} from 'react'
import {NavigationContainer,useNavigation } from '@react-navigation/native';
import {UserIcon, MagnifyingGlassIcon} from 'react-native-heroicons/outline'
import Categories from '../components/Categories.jsx'


const HomeScreens = () => {
  const navigation = useNavigation();
  useLayoutEffect(() => {
    navigation.setOptions({
      headerShown: false
    })
  }, [])

  return (
    <SafeAreaView className="bg-white">
      <View className="flex justify-center items-center gap-2 border">
        <View className="w-7 h-7 bg-rose-500 rounded-3xl items-center">
          {/* <Image source={{uri:}}/> */}
          <UserIcon size={25}color="black"/>
          {/* <SparklesIcon/> */}
        </View>
        <Text>PlaceHolder Username</Text>

      </View>
      <View className="flex justify-center items-center px-4">
        <View className="flex-row bg-gray-200 p-3 mt-2 w-full">
          <MagnifyingGlassIcon color="black"/>
          <TextInput placeholder="Search For Whatever" keyboardType="default"/>
        </View>
      </View>
      <Categories/>
    </SafeAreaView>
  )
}

export default HomeScreens
